var base_url = "https://doctruyen29.blogspot.com/";
if (typeof Application == "undefined") {
    Application = {
        run: function () {
            this.sliderHome();
            this.viewMoreDesc();
            this.search();
            this.scrollChapterList();
            this.menuMobile();
        },

        sliderHome: function () {
            if ($(".box-manga-carousel-items").length) {
                $(".box-manga-carousel-items").slick({
                    infinite: true,
                    slidesToShow: 5,
                    slidesToScroll: 5,
                    arrows: true,
                    dots: false,
                    prevArrow: '<a id="prevTop" class="prev" href="javascript:void(0);" rel="nofollow" style="display: block;"><i class="fa fa-angle-left" aria-hidden="true"></i></a>',
                    nextArrow: '<a id="nextTop" class="next" href="javascript:void(0);" rel="nofollow" style="display: block;"><i class="fa fa-angle-right" aria-hidden="true"></i></a>',
                    responsive: [
                        {
                            breakpoint: 1024,
                            settings: {
                                slidesToShow: 3,
                                slidesToScroll: 3,
                                infinite: true,
                                dots: true,
                            },
                        },
                        {
                            breakpoint: 600,
                            settings: {
                                slidesToShow: 2,
                                slidesToScroll: 2,
                            },
                        },
                        {
                            breakpoint: 480,
                            settings: {
                                slidesToShow: 2,
                                slidesToScroll: 2,
                            },
                        },
                    ],
                });
            }
        },

        viewMoreDesc: function () {
            if ($("#desc-text-manga").length) {
                $(".btn-readmore").on("click", function (e) {
                    e.preventDefault();
                    if (this.innerHTML == "xem thêm") {
                        $(".summary-content").css("height", "auto");
                        this.innerHTML = "rút gọn";
                    } else {
                        $(".summary-content").css("height", "50px");
                        this.innerHTML = "xem thêm";
                        $("html, body").animate({ scrollTop: $("#desc-text-manga").offset().top }, "slow");
                    }
                });
            }
        },
        menuMobile: function () {
            if ($("#mobile-menu").length) {
                $("#mobile-menu").on("click", function () {
                    $("#navbar-left").toggle();
                });
            }
        },
        search: function () {
            $("#btn-search").on("click", function () {
                var keyword = $("#search").val();
                if (keyword == "") {
                    return false;
                }
                $("#site-search").submit();
            });
        },

        scrollChapterList: function () {
            if ($("#btn-chapters-list").length) {
                $("#btn-chapters-list").on("click", function (a) {
                    a.preventDefault();
                    $("html, body").animate(
                        {
                            scrollTop: $("#chapters-list").offset().top,
                        },
                        "slow"
                    );
                });
            }
        },
    };
    Application.run();
}

function backtotop() {
    jQuery("html, body").animate({ scrollTop: 0 }, 100);
}

jQuery(window).scroll(function () {
    if (jQuery(this).scrollTop() > 100) {
        jQuery("#toTop").css("opacity", 1);
    } else {
        jQuery("#toTop").css("opacity", 0);
    }
});

function openTool(el) {
    if (jQuery(el).parent().hasClass("show-sub")) {
        closeTool();
    } else {
        jQuery(".item-control").removeClass("show-sub");
        jQuery(el).parent().addClass("show-sub");
    }
}
function closeTool() {
    jQuery(".item-control").removeClass("show-sub");
    //saveSetting();
}
function changeTheme(name) {
    if (jQuery(".value-" + name).hasClass("active")) {
    } else {
        jQuery(".theme-value").removeClass("active");
        jQuery(".box-chapter-c").removeClass("theme-01");
        jQuery(".box-chapter-c").removeClass("theme-02");
        jQuery(".box-chapter-c").removeClass("theme-03");
        jQuery(".box-chapter-c").removeClass("theme-04");
        jQuery(".box-chapter-c").removeClass("theme-05");
        jQuery(".box-chapter-c").removeClass("theme-06");
        jQuery(".box-chapter-c").removeClass("theme-07");
        jQuery(".box-chapter-c").addClass("theme-" + name);
        jQuery(".value-" + name).addClass("active");
        if (jQuery(".value-" + name).hasClass("value-07")) {
            jQuery("body").addClass("body-07");
            jQuery("body").removeClass("body-06");
        } else if (jQuery(".value-" + name).hasClass("value-06")) {
            jQuery("body").addClass("body-06");
            jQuery("body").removeClass("body-07");
        } else {
            jQuery("body").removeClass("body-06");
            jQuery("body").removeClass("body-07");
        }
    }
}
function changeFont(name) {
    if (jQuery(".value-" + name).hasClass("active")) {
    } else {
        jQuery(".font-value").removeClass("active");
        jQuery(".box-chapter-content").removeClass("palatino");
        jQuery(".box-chapter-content").removeClass("times");
        jQuery(".box-chapter-content").removeClass("arial");
        jQuery(".box-chapter-content").removeClass("georgia");

        jQuery(".box-chapter-content").addClass(name);
        jQuery(".font-" + name).addClass("active");
    }
}
function changeSize(flag) {
    var number = parseInt(jQuery(".box-size .lang").text());
    if (flag) {
        if (number < 48) {
            number = number + 2;
        }
    } else {
        if (number > 12) {
            number = number - 2;
        }
    }
    jQuery(".box-size .lang").text(number);
    jQuery(".box-chapter-content").css("font-size", number + "px");
}
function changeWidth(flag) {
    var number = parseInt(jQuery(".box-width .lang").text());
    var w_width = jQuery(window).width();
    if (flag) {
        switch (number) {
            case 640:
                number = 800;
                break;
            case 800:
                number = 900;
                break;
            case 900:
                number = 900;
                break;
            default:
                number = 900;
                break;
        }
    } else {
        switch (number) {
            case 800:
                number = 640;
                break;
            case 900:
                number = 800;
                break;
            //  case 1280: number = 900; break;
            default:
                number = 640;
                break;
        }
    }
    if (w_width < 1400 && number > 900) {
        number = 900;
    } else {
        jQuery(".box-chapter-content").removeClass("max900");
    }
    if (w_width < 1000 && number > 800) {
        number = 800;
    } else {
        jQuery(".box-chapter-content").removeClass("max800");
    }
    jQuery(".box-width .lang").text(number);
    jQuery(".box-chapter-content").removeClass("w640");
    jQuery(".box-chapter-content").removeClass("w800");
    jQuery(".box-chapter-content").removeClass("w900");
    jQuery(".box-chapter-content").removeClass("w1280");
    jQuery(".box-chapter-content").addClass("w" + number);
}

